import React, { useEffect, useState } from "react";

const Menu = (props) => {
  let [teamList, setTeam] = useState([
    "49ers",
    "Bears",
    "Bengals",
    "Bills",
    "Broncos",
    "Browns",
    "Buccaneers",
    "Cardinals",
    "Chargers",
    "Chiefs",
    "Colts",
    "Commanders",
    "Cowboys",
    "Dolphins",
    "Eagles",
    "Falcons",
    "Giants",
    "Jaguars",
    "Jets",
    "Lions",
    "Packers",
    "Panthers",
    "Patriots",
    "Raiders",
    "Rams",
    "Ravens",
    "Saints",
    "Seahawks",
    "Steelers",
    "Texans",
    "Titans",
    "Vikings",
  ]);
  let [text, setText] = useState("");
  let [display, setDisplay] = useState([]);
  let search = (text) => {
    let count = 0;
    setDisplay([]);
    for (let i = 0; i < 32; i++) {
      if (text === teamList[i].slice(0, text.length)) {
        setDisplay((teams) => [...teams, teamList[i]]);
        count++;
        if (count > 5) {
          break;
        }
      }
    }
  };

  let handleChange = (event) => {
    setText(event.target.value);
  };

  useEffect(() => {
    search(text);
  }, [text]);

  return (
    <div
      class="overlay"
      onClick={() => {
        if (props.isOpen == true) {
          props.setOpen(false);
        }
      }}
    >
      <div
        class="modalContainer flex flex-col items-center"
        onClick={(e) => {
          e.stopPropagation();
        }}
      >
        <form onSubmit={search} class="flex items-center w-screen mt-[16px]">
          <input
            onChange={handleChange}
            value={text}
            class="rounded-full pl-2 py-1 mt[1em] border-black border-[1px] border-solid w-full mx-[16px]"
          ></input>
          <button type="submit" class="search absolute right-[30px]"></button>
        </form>
        <div>
          {teamList && (
            <div className={`flex flex-col overflow-scroll main-content`}>
              {teamList.map((message) => {
                return (
                  <div class="">
                    <p class="bg-[#2B8FFC] rounded-xl w-[90%] my-2 text-left">
                      {message}
                    </p>
                  </div>
                );
              })}
            </div>
          )}
        </div>
      </div>
    </div>
  );
};

export default Menu;
