import React, { useState, useEffect } from "react";
import io from "socket.io-client";

const socket = io.connect("http://localhost:3001");
function App() {
  let [text, setText] = useState("");
  let [formData, setFormData] = useState([]);

  useEffect(() => {
    updateChat();
  }, []);

  useEffect(() => {
    socket.on("recevied-message", async (data) => {
      updateChat(data);
    });
  }, [socket]);

  let updateChat = async () => {
    let res = await fetch("http://localhost:3001/getMessage", {
      method: "GET",
      headers: {
        "Content-Type": "application/json",
      },
    });
    setFormData(await res.json());
  };
  let handleChange = (event) => {
    // To get user input value to state use
    setText(event.target.value);
  };
  // this is a test branch with test line 11
  let postMessage = async (event) => {
    // Too add message to list of messages
    event.preventDefault();
    if (text.length > 0) {
      let bodyData = { message: text };
      let res = await fetch("http://localhost:3001/sendmessage", {
        method: "POST",
        body: JSON.stringify(bodyData),
        headers: {
          "Content-Type": "application/json",
        },
      });
      socket.emit("send-message", "data");
      updateChat(res);
    } else {
      console.log("Please Enter A Message");
    }
    setText("");
  };

  return (
    <div>
      <form onSubmit={postMessage}>
        <input onChange={handleChange} value={text}></input>
        <button>Confirm</button>
      </form>
      {formData && (
        <p>
          {formData.map((message) => {
            return <p>{message.message}</p>;
          })}
        </p>
      )}
    </div>
  );
}

export default App;
