import React, { useState } from "react";

const Menu = (props) => {
  let [text, setText] = useState();
  let search = () => {
    console.log("search");
  };

  let handleChange = (event) => {
    // To search for a team
    setText(event.target.value);
  };
  return (
    <div
      class="overlay"
      onClick={() => {
        if (props.isOpen == true) {
          props.setOpen(false);
        }
      }}
    >
      <div
        class="modalContainer flex flex-col items-center"
        onClick={(e) => {
          e.stopPropagation();
        }}
      >
        <form onSubmit={search} class="flex items-center w-screen mt-[16px]">
          <input
            onChange={handleChange}
            value={text}
            class="rounded-full pl-2 py-1 mt[1em] border-black border-[1px] border-solid w-full mx-[16px]"
          ></input>
          <button type="submit" class="search absolute right-[30px]"></button>
        </form>
      </div>
    </div>
  );
};

export default Menu;
